targets.expr:
sum(
  ${rate_func}(
    k8s_node_cpu_time_seconds_total{
      k8s_node_name="$node",
      app_name=~"$app_name",
      app_version=~"$app_version",
      region=~"$region",
      cluster_id=~"$cluster_id",
      env=~"$env"
    }[$__rate_interval]
  )
) / sum(
  kube_node_status_capacity{
    resource="cpu",
    node="$node",
    app_name=~"$app_name",
    app_version=~"$app_version",
    region=~"$region",
    cluster_id=~"$cluster_id",
    env=~"$env"
  }
)

