sum(
  ${rate_func}(
    container_cpu_time_seconds_total{
      k8s_namespace_name="$namespace",
      k8s_pod_name="$pod",
      app_name=~"$app_name",
      app_version=~"$app_version",
      region=~"$region",
      cluster_id=~"$cluster_id",
      env=~"$env"
    }[$__rate_interval]
  )
) / sum(
  kube_pod_container_resource_requests{
    namespace="$namespace",
    pod="$pod",
    resource="cpu",
    app_name=~"$app_name",
    app_version=~"$app_version",
    region=~"$region",
    cluster_id=~"$cluster_id",
    env=~"$env"
  }
  and on(namespace, pod) max by (namespace, pod) (
    kube_pod_status_phase{
      phase="Running",
      namespace="$namespace",
      pod="$pod",
      app_name=~"$app_name",
      app_version=~"$app_version",
      region=~"$region",
      cluster_id=~"$cluster_id",
      env=~"$env"
    } == 1
  )
)

